sourcesContent:
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  ArrowRight,
  CheckCircle,
  Play,
  Brain,
  Palette,
  Code2,
  Search,
  Server,
  Megaphone,
  Code,
  ShoppingCart,
  Users,
  Layout,
  Headphones,
  ArrowUpRight
} from 'lucide-react';
import useScrollAnimation from '../hooks/useScrollAnimation';

const CONTACT_API_URL =
  process.env.REACT_APP_CONTACT_API_URL || 'http://localhost:5000/api/contact';

const Home = () => {
  useScrollAnimation();

  const offerings = [
    {
      title: 'Development',
      description: 'Pixel-perfect engineering across modern stacks and workflows.',
      icon: Code
    },
    {
      title: 'Woo Commerce',
      description: 'High-converting storefronts tailored for memorable shopping journeys.',
      icon: ShoppingCart
    },
    {
      title: 'CRM Solutions',
      description: 'Customer experience platforms that centralize insights for success.',
      icon: Users
    },
    {
      title: 'Web Design',
      description: 'Vibrant, intuitive, and minimal digital expressions of your brand.',
      icon: Layout
    },
    {
      title: 'IT Support',
      description: 'Always-on experts ready to troubleshoot and optimize your systems.',
      icon: Headphones,
      tag: 'Free!'
    }
  ];

  const brandLogos = [
    { name: 'Netflix', logo: '/brands/netflix.svg' },
    { name: 'Apple Music', logo: '/brands/apple-music.svg' },
    { name: 'Amazon', logo: '/brands/amazon.svg' },
    { name: 'Spotify', logo: '/brands/spotify.svg' },
    { name: 'Google', logo: 'https://logo.clearbit.com/google.com' },
    { name: 'Microsoft', logo: 'https://logo.clearbit.com/microsoft.com' },
    { name: 'Adobe', logo: 'https://logo.clearbit.com/adobe.com' },
    { name: 'Salesforce', logo: 'https://logo.clearbit.com/salesforce.com' },
    { name: 'IBM', logo: 'https://logo.clearbit.com/ibm.com' },
    { name: 'Oracle', logo: 'https://logo.clearbit.com/oracle.com' }
  ];

  const consultingHighlights = [
    '24/7 Full Service Support',
    'Immediate Response',
    'Easy to approach us'
  ];

  const caseStudies = [
    {
      name: 'Development',
      badgePrimary: 'Web Development',
      badgeSecondary: 'Mobile Development',
      imagePrimary:
        'https://images.unsplash.com/photo-1517433456452-f9633a875f6f?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?auto=format&fit=crop&w=800&q=80',
      description:
        'Software development is a dynamic and rapidly evolving field that shapes the digital landscape. We design, create, deploy, and maintain modern applications and systems.'
    },
    {
      name: 'Woo Commerce',
      badgePrimary: 'Storefront Design',
      badgeSecondary: 'Conversion Optimization',
      imagePrimary:
        'https://images.unsplash.com/photo-1545239351-1141bd82e8a6?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1441986300917-64674bd600d8?auto=format&fit=crop&w=800&q=80',
      description:
        'We build immersive WooCommerce experiences that balance brand storytelling with intuitive purchasing journeys and robust integrations.'
    },
    {
      name: 'CRM Solutions',
      badgePrimary: 'Customer Insights',
      badgeSecondary: 'Automation Pipelines',
      imagePrimary:
        'https://images.unsplash.com/photo-1556740749-887f6717d7e4?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1587614382346-ac6c4593abfc?auto=format&fit=crop&w=800&q=80',
      description:
        'Our CRM programs unify sales, service, and marketing data to power personalization and operational efficiency across touchpoints.'
    },
    {
      name: 'Web Designing',
      badgePrimary: 'Brand Systems',
      badgeSecondary: 'Prototype Labs',
      imagePrimary:
        'https://images.unsplash.com/photo-1523475472560-d2df97ec485c?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1498050108023-c5249f4df085?auto=format&fit=crop&w=800&q=80',
      description:
        'We pair design thinking with motion, accessibility, and consistent UI systems to craft standout digital destinations.'
    },
    {
      name: 'IT Support',
      badgePrimary: 'Managed Services',
      badgeSecondary: 'Security Operations',
      imagePrimary:
        'https://images.unsplash.com/photo-1525182008055-f88b95ff7980?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1518770660439-4636190af475?auto=format&fit=crop&w=800&q=80',
      description:
        'Our 24/7 IT support covers proactive monitoring, rapid incident response, and lifecycle management for hybrid infrastructures.'
    }
  ];

  const portfolioGrid = [
    {
      image: 'https://s.wordpress.com/mshots/v1/https%3A%2F%2Fvionindustries.in%2F?w=1200',
      title: 'Vion Industries',
      description: 'Manufacturer portfolio and service showcase for injection moulding specialists.',
      url: 'https://vionindustries.in/'
    },
    {
      image: 'https://images.unsplash.com/photo-1556740749-887f6717d7e4?auto=format&fit=crop&w=600&q=80',
      title: 'E-commerce Expansion',
      description: 'Headless commerce build driving 3x revenue lift and omnichannel customer journeys.'
    },
    {
      image: 'https://images.unsplash.com/photo-1581090465460-7e88c1d41a4f?auto=format&fit=crop&w=1200&q=80',
      title: 'Healthcare Telemedicine',
      description: 'HIPAA-compliant virtual care platform scaling to 1M+ monthly appointments.'
    },
    {
      image: 'https://images.unsplash.com/photo-1531297484001-80022131f5a1?auto=format&fit=crop&w=600&q=80',
      title: 'SaaS Workflow Automation',
      description: 'AI copilots and integrations that shaved 35% off manual operations for enterprises.'
    },
    {
      image: 'https://images.unsplash.com/photo-1515378791036-0648a3ef77b2?auto=format&fit=crop&w=600&q=80',
      title: 'EdTech Learning Hub',
      description: 'Adaptive classroom experiences supporting personalized learning paths worldwide.'
    },
    {
      image: 'https://images.unsplash.com/photo-1545239351-1141bd82e8a6?auto=format&fit=crop&w=600&q=80',
      title: 'Retail Loyalty Platform',
      description: 'Unified loyalty ecosystem with real-time offers and customer insights.'
    },
    {
      image: 'https://images.unsplash.com/photo-1550751827-4bd374c3f58b?auto=format&fit=crop&w=600&q=80',
      title: 'Smart City Operations',
      description: 'IoT command center orchestrating utilities, mobility, and public services.'
    },
    {
      image: 'https://images.unsplash.com/photo-1573497491208-6b1acb260507?auto=format&fit=crop&w=600&q=80',
      title: 'Corporate Brand Refresh',
      description: 'Enterprise website overhaul amplifying story, conversion, and accessibility.'
    },
    {
      image: 'https://images.unsplash.com/photo-1591696331075-62469d60c3c5?auto=format&fit=crop&w=1200&q=80',
      title: 'AI Customer Support Suite',
      description: 'Conversational agents and knowledge automation reducing support backlog by 55%.'
    },
    {
      image: 'https://images.unsplash.com/photo-1553877522-43269d4ea984?auto=format&fit=crop&w=1200&q=80',
      title: 'Cloud Migration Program',
      description: 'Hybrid cloud blueprint accelerating deployments and improving resilience globally.'
    },
    {
      image: 'https://images.unsplash.com/photo-1521737604893-d14cc237f11d?auto=format&fit=crop&w=1200&q=80',
      title: 'Product Analytics Platform',
      description: 'Real-time product intelligence with governance for enterprise experimentation.'
    },
    {
