import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, ChevronDown, ArrowRight } from 'lucide-react';
import logo from '../assets/logo.svg';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [servicesOpen, setServicesOpen] = useState(false);
  const [portfolioOpen, setPortfolioOpen] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const servicesMenu = {
    badge: 'What we do',
    headline: 'Services',
    description: '',
    items: [
      {
        name: 'Software Development',
        description: 'Strategic engineering partners for new builds and modernization.',
        image: 'https://images.unsplash.com/photo-1521737604893-d14cc237f11d?auto=format&fit=crop&w=320&q=80',
        href: '/services#software'
      },
      {
        name: 'Web, Mobile & Desktop Apps',
        description: 'Unified experiences across responsive web, native mobile, and desktop.',
        image: 'https://images.unsplash.com/photo-1525182008055-f88b95ff7980?auto=format&fit=crop&w=320&q=80',
        href: '/services#apps'
      },
      {
        name: 'Custom ERP & CRM Systems',
        description: 'Operational platforms tailored to your processes and data flows.',
        image: 'https://images.unsplash.com/photo-1518186285589-2f7649de83e0?auto=format&fit=crop&w=320&q=80',
        href: '/services#erp-crm'
      },
      {
        name: 'API & System Integration',
        description: 'Connect cloud, legacy, and third-party systems with secure APIs.',
        image: 'https://images.unsplash.com/photo-1517430816045-df4b7de11d1d?auto=format&fit=crop&w=320&q=80',
        href: '/services#integration'
      },
      {
        name: 'Website Design & Development',
        description: 'Brand-forward websites with performance, SEO, and accessibility baked in.',
        image: 'https://images.unsplash.com/photo-1523475472560-d2df97ec485c?auto=format&fit=crop&w=320&q=80',
        href: '/services#websites'
      },
      {
        name: 'Corporate, E-commerce & Portfolio Websites',
        description: 'Launch or refresh sites for enterprises, retailers, and creative teams.',
        image: 'https://images.unsplash.com/photo-1521737604893-ffcf00a1a497?auto=format&fit=crop&w=320&q=80',
        href: '/services#specialty-sites'
      },
      {
        name: 'SEO, Speed & Security Optimization',
        description: 'Boost rankings, load times, and resilience with technical hardening.',
        image: 'https://images.unsplash.com/photo-1526481280695-3c46997a8e33?auto=format&fit=crop&w=320&q=80',
        href: '/services#optimization'
      },
      {
        name: 'AI Automations',
        description: 'Intelligent workflows, copilots, and analytics embedded into your tools.',
        image: 'https://images.unsplash.com/photo-1526378722415-4bdc0b45d559?auto=format&fit=crop&w=320&q=80',
        href: '/services#ai-automations'
      }
    ]
  };

  const portfolioMenu = {
    badge: 'Latest work',
    headline: 'Projects that move businesses forward',
    description: 'Browse a curated collection of recent launches spanning industries and audiences.',
    items: [
      {
        name: 'E-commerce',
        description: 'Migrated a legacy retailer to a modern headless stack with 3x conversions.',
        image: 'https://images.unsplash.com/photo-1557821552-17105176677c?auto=format&fit=crop&w=320&q=80',
        href: '/portfolio#ecommerce'
      },
      {
        name: 'Healthcare',
        description: 'Connected patient experiences with secure tele-medicine integrations.',
        image: 'https://images.unsplash.com/photo-1576091160399-112ba8d25d1d?auto=format&fit=crop&w=320&q=80',
        href: '/portfolio#health'
      },
      {
        name: 'Finance',
        description: 'Delivered a compliant digital banking platform with real-time insights.',
        image: 'https://images.unsplash.com/photo-1563013544-824ae1b704d3?auto=format&fit=crop&w=320&q=80',
        href: '/portfolio#finance'
      },
      {
        name: 'Education',
        description: 'Launched an adaptive learning environment for global classrooms.',
        image: 'https://images.unsplash.com/photo-1503676260728-1c00da094a0b?auto=format&fit=crop&w=320&q=80',
        href: '/portfolio#education'
      }
    ],
    highlight: {
      title: 'Mobile app for a new era',
      description: 'See how we increased daily active usage by 42% with a refreshed experience.',
      image: 'https://images.unsplash.com/photo-1582719478250-c89cae4dc85b?auto=format&fit=crop&w=640&q=80',
      href: '/portfolio/mobile-app'
    }
  };

  return (
    <nav className={`fixed w-full z-[100] transition-all duration-300 ${
      scrolled ? 'bg-white shadow-lg' : 'bg-white/95 backdrop-blur-sm'
    }`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-20">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3">
            <img src={logo} alt="EngiNerds" className="h-12 w-auto" />
            <span className="sr-only">EngiNerds</span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden lg:flex items-center space-x-8">
            <Link
              to="/"
              className={`text-sm font-medium transition-colors duration-200 ${
                location.pathname === '/'
                  ? 'text-primary-500'
                  : 'text-slate-600 hover:text-primary-500'
              }`}
            >
              Home
            </Link>

            {/* Services Mega Menu */}
            <div 
              className="relative"
              onMouseEnter={() => setServicesOpen(true)}
              onMouseLeave={() => setServicesOpen(false)}
            >
              <button className="flex items-center text-sm font-medium text-slate-600 hover:text-primary-500 transition-colors duration-200 py-2">
                Services
                <ChevronDown className="ml-1 w-4 h-4" />
              </button>

              {servicesOpen && (
                <div className="absolute left-1/2 top-full z-50 w-[85vw] max-w-6xl -translate-x-1/2 pt-3">
                  <div className="bg-white border border-gray-100 shadow-[0_24px_60px_-24px_rgba(15,23,42,0.35)] rounded-3xl overflow-hidden">
                    <div className="p-6 lg:p-8">
                      <p className="text-xs font-semibold uppercase tracking-wide text-primary-500">{servicesMenu.badge}</p>
                      <h3 className="mt-3 text-2xl font-semibold text-gray-900">{servicesMenu.headline}</h3>
                      {servicesMenu.description && (
                        <p className="mt-2 text-sm text-gray-500 leading-relaxed">{servicesMenu.description}</p>
                      )}
                      <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-4">
                        {servicesMenu.items.map((item) => (
                          <Link
                            key={item.name}
                            to={item.href}
                            className="group flex items-start gap-4 rounded-xl border border-gray-100 bg-white p-4 hover:border-primary-100 hover:bg-primary-50/40 transition-colors"
                          >
                            <div className="relative h-16 w-16 flex-shrink-0 overflow-hidden rounded-xl bg-gray-100">
                              <img src={item.image} alt={item.name} className="h-full w-full object-cover" />
                            </div>
                            <div>
                              <p className="text-sm font-semibold text-gray-900 transition-colors group-hover:text-primary-500">{item.name}</p>
                              <p className="mt-1 text-sm text-gray-500 leading-snug">{item.description}</p>
                            </div>
                          </Link>
                        ))}
                      </div>
                      <div className="mt-8 flex flex-col gap-4 border-t border-gray-100 pt-5 sm:flex-row sm:items-center sm:justify-between">
                        <div className="flex items-center gap-3 text-sm text-gray-500">
                          <span className="inline-flex h-8 w-8 items-center justify-center rounded-full bg-primary-50 text-primary-500 font-semibold">24/7</span>
                          Dedicated support for every engagement
                        </div>
                        <Link to="/services" className="inline-flex items-center text-sm font-semibold text-primary-500 hover:text-primary-600">
                          View all services
                          <ArrowRight className="ml-2 h-4 w-4" />
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Portfolio Mega Menu */}
            <div 
              className="relative"
              onMouseEnter={() => setPortfolioOpen(true)}
              onMouseLeave={() => setPortfolioOpen(false)}
            >
              <button className="flex items-center text-sm font-medium text-slate-600 hover:text-primary-500 transition-colors duration-200 py-2">
                Portfolio
                <ChevronDown className="ml-1 w-4 h-4" />
              </button>

              {portfolioOpen && (
                <div className="absolute left-1/2 top-full z-50 w-[85vw] max-w-6xl -translate-x-1/2 pt-3">
                  <div className="bg-white border border-gray-100 shadow-[0_24px_60px_-24px_rgba(15,23,42,0.35)] rounded-3xl overflow-hidden">
                    <div className="grid lg:grid-cols-[7fr,5fr]">
                      <div className="p-6 lg:p-8">
                        <p className="text-xs font-semibold uppercase tracking-wide text-primary-500">{portfolioMenu.badge}</p>
                        <h3 className="mt-3 text-2xl font-semibold text-gray-900">{portfolioMenu.headline}</h3>
                        <p className="mt-2 text-sm text-gray-500 leading-relaxed">{portfolioMenu.description}</p>
                        <div className="mt-6 grid sm:grid-cols-2 gap-4">
                          {portfolioMenu.items.map((item) => (
                            <Link
                              key={item.name}
                              to={item.href}
                              className="group flex items-start gap-4 rounded-xl border border-gray-100 bg-white p-4 hover:border-primary-100 hover:bg-primary-50/40 transition-colors"
                            >
                              <div className="relative h-16 w-16 flex-shrink-0 overflow-hidden rounded-xl bg-gray-100">
                                <img src={item.image} alt={item.name} className="h-full w-full object-cover" />
                              </div>
                              <div>
                                <p className="text-sm font-semibold text-gray-900 transition-colors group-hover:text-primary-500">{item.name}</p>
                                <p className="mt-1 text-sm text-gray-500 leading-snug">{item.description}</p>
                              </div>
                            </Link>
                          ))}
                        </div>
                        <div className="mt-8 flex flex-col gap-4 border-t border-gray-100 pt-5 sm:flex-row sm:items-center sm:justify-between">
                          <div className="flex items-center gap-3 text-sm text-gray-500">
                            <span className="inline-flex h-8 w-8 items-center justify-center rounded-full bg-primary-50 text-primary-500 font-semibold">150+</span>
                            Case studies delivered globally
                          </div>
                          <Link to="/portfolio" className="inline-flex items-center text-sm font-semibold text-primary-500 hover:text-primary-600">
                            View portfolio
                            <ArrowRight className="ml-2 h-4 w-4" />
                          </Link>
                        </div>
                      </div>
                      <div className="flex flex-col justify-between border-t border-gray-100 bg-gray-50 p-6 lg:border-t-0 lg:border-l lg:p-8">
                        <div>
                          <div className="relative overflow-hidden rounded-2xl">
                            <img src={portfolioMenu.highlight.image} alt={portfolioMenu.highlight.title} className="h-44 w-full object-cover" />
                          </div>
                          <h4 className="mt-6 text-lg font-semibold text-gray-900">{portfolioMenu.highlight.title}</h4>
                          <p className="mt-2 text-sm text-gray-500 leading-relaxed">{portfolioMenu.highlight.description}</p>
                        </div>
                        <Link to={portfolioMenu.highlight.href} className="mt-6 inline-flex items-center text-sm font-semibold text-primary-500 hover:text-primary-600">
                          View more
                          <ArrowRight className="ml-2 h-4 w-4" />
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>

            <Link
              to="/about"
              className={`text-sm font-medium transition-colors duration-200 ${
                location.pathname === '/about'
                  ? 'text-primary-500'
                  : 'text-slate-600 hover:text-primary-500'
              }`}
            >
              About
            </Link>
            <Link
              to="/career"
              className={`text-sm font-medium transition-colors duration-200 ${
                location.pathname === '/career'
                  ? 'text-primary-500'
                  : 'text-slate-600 hover:text-primary-500'
              }`}
            >
              Career
            </Link>
            <Link
              to="/contact"
              className="bg-primary-500 text-white px-6 py-2.5 rounded-lg font-medium hover:bg-primary-600 transition-colors duration-200"
            >
              Contact Us
            </Link>
          </div>

          {/* Mobile menu button */}
          <button
            onClick={() => setIsOpen(!isOpen)}
            className="lg:hidden p-2 rounded-lg text-slate-600 hover:bg-gray-100"
          >
            {isOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isOpen && (
        <div className="lg:hidden bg-white border-t">
          <div className="px-4 py-4 space-y-3">
            <Link to="/" onClick={() => setIsOpen(false)} className="block px-4 py-2 rounded-lg text-sm font-medium text-slate-600 hover:bg-gray-50">
              Home
            </Link>
            <Link to="/services" onClick={() => setIsOpen(false)} className="block px-4 py-2 rounded-lg text-sm font-medium text-slate-600 hover:bg-gray-50">
              Services
            </Link>
            <Link to="/portfolio" onClick={() => setIsOpen(false)} className="block px-4 py-2 rounded-lg text-sm font-medium text-slate-600 hover:bg-gray-50">
              Portfolio
            </Link>
            <Link to="/about" onClick={() => setIsOpen(false)} className="block px-4 py-2 rounded-lg text-sm font-medium text-slate-600 hover:bg-gray-50">
              About
            </Link>
            <Link to="/career" onClick={() => setIsOpen(false)} className="block px-4 py-2 rounded-lg text-sm font-medium text-slate-600 hover:bg-gray-50">
              Career
            </Link>
            <Link
              to="/contact"
              onClick={() => setIsOpen(false)}
              className="block w-full bg-primary-500 text-white px-4 py-2.5 rounded-lg font-medium text-center hover:bg-primary-600"
            >
              Contact Us
            </Link>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
