import React from 'react';
import { ArrowRight } from 'lucide-react';

const ServiceHero = ({ 
  title, 
  subtitle, 
  description, 
  backgroundImage, 
  ctaText = "Get Started", 
  ctaLink = "#contact" 
}) => {
  return (
    <section className="relative overflow-hidden pt-32 pb-24 lg:pt-40 lg:pb-32">
      <img
        src={backgroundImage}
        alt={title}
        className="absolute inset-0 h-full w-full object-cover"
      />
      <div className="absolute inset-0 bg-gradient-to-r from-slate-900/95 via-slate-900/90 to-slate-900/70"></div>
      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="max-w-3xl">
          <div className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-primary-500/20 text-primary-300 border border-primary-500/30 shadow-sm mb-6">
            <span className="w-2 h-2 bg-primary-400 rounded-full mr-2 animate-pulse"></span>
            {subtitle}
          </div>
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white leading-tight mb-6">
            {title}
          </h1>
          <p className="text-lg md:text-xl text-slate-200 leading-relaxed mb-8">
            {description}
          </p>
          <div className="flex flex-col sm:flex-row gap-4">
            <a
              href={ctaLink}
              className="inline-flex items-center justify-center px-8 py-4 rounded-full bg-primary-500 text-white font-semibold shadow-[0_20px_45px_-25px_rgba(249,115,22,0.8)] transition-colors duration-200 hover:bg-primary-600"
            >
              {ctaText}
              <ArrowRight className="ml-2 h-5 w-5" />
            </a>
            <a
              href="https://wa.me/918567927943"
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center justify-center px-8 py-4 rounded-full border-2 border-green-500 text-green-400 font-semibold transition-colors duration-200 hover:bg-green-500/10"
            >
              WhatsApp Us
              <ArrowRight className="ml-2 h-5 w-5" />
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ServiceHero;
