import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  ArrowRight,
  CheckCircle,
  Play,
  Brain,
  Palette,
  Code2,
  Search,
  Server,
  Megaphone,
  Code,
  ShoppingCart,
  Users,
  Layout,
  Headphones,
  ArrowUpRight
} from 'lucide-react';
import useScrollAnimation from '../hooks/useScrollAnimation';

const Home = () => {
  useScrollAnimation();

  const stats = [
    { number: '300+', label: 'Projects' },
    { number: '150+', label: 'Clients' },
    { number: '15+', label: 'Years' },
    { number: '98%', label: 'Success' }
  ];

  const offerings = [
    {
      title: 'Development',
      description: 'Pixel-perfect engineering across modern stacks and workflows.',
      icon: Code
    },
    {
      title: 'Woo Commerce',
      description: 'High-converting storefronts tailored for memorable shopping journeys.',
      icon: ShoppingCart
    },
    {
      title: 'CRM Solutions',
      description: 'Customer experience platforms that centralize insights for success.',
      icon: Users
    },
    {
      title: 'Web Design',
      description: 'Vibrant, intuitive, and minimal digital expressions of your brand.',
      icon: Layout
    },
    {
      title: 'IT Support',
      description: 'Always-on experts ready to troubleshoot and optimize your systems.',
      icon: Headphones,
      tag: 'Free!'
    }
  ];

  const caseStudies = [
    {
      name: 'Development',
      badgePrimary: 'Web Development',
      badgeSecondary: 'Mobile Development',
      imagePrimary:
        'https://images.unsplash.com/photo-1517433456452-f9633a875f6f?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?auto=format&fit=crop&w=800&q=80',
      description:
        'Software development is a dynamic and rapidly evolving field that shapes the digital landscape. We design, create, deploy, and maintain modern applications and systems.'
    },
    {
      name: 'Woo Commerce',
      badgePrimary: 'Storefront Design',
      badgeSecondary: 'Conversion Optimization',
      imagePrimary:
        'https://images.unsplash.com/photo-1545239351-1141bd82e8a6?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1441986300917-64674bd600d8?auto=format&fit=crop&w=800&q=80',
      description:
        'We build immersive WooCommerce experiences that balance brand storytelling with intuitive purchasing journeys and robust integrations.'
    },
    {
      name: 'CRM Solutions',
      badgePrimary: 'Customer Insights',
      badgeSecondary: 'Automation Pipelines',
      imagePrimary:
        'https://images.unsplash.com/photo-1556740749-887f6717d7e4?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1587614382346-ac6c4593abfc?auto=format&fit=crop&w=800&q=80',
      description:
        'Our CRM programs unify sales, service, and marketing data to power personalization and operational efficiency across touchpoints.'
    },
    {
      name: 'Web Designing',
      badgePrimary: 'Brand Systems',
      badgeSecondary: 'Prototype Labs',
      imagePrimary:
        'https://images.unsplash.com/photo-1523475472560-d2df97ec485c?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1498050108023-c5249f4df085?auto=format&fit=crop&w=800&q=80',
      description:
