import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  ArrowRight,
  CheckCircle,
  Play,
  Brain,
  Palette,
  Code2,
  Search,
  Server,
  Megaphone,
  Code,
  ShoppingCart,
  Users,
  Layout,
  Headphones,
  ArrowUpRight
} from 'lucide-react';
import useScrollAnimation from '../hooks/useScrollAnimation';

const Home = () => {
  useScrollAnimation();

  const offerings = [
    {
      title: 'Development',
      description: 'Pixel-perfect engineering across modern stacks and workflows.',
      icon: Code
    },
    {
      title: 'Woo Commerce',
      description: 'High-converting storefronts tailored for memorable shopping journeys.',
      icon: ShoppingCart
    },
    {
      title: 'CRM Solutions',
      description: 'Customer experience platforms that centralize insights for success.',
      icon: Users
    },
    {
      title: 'Web Design',
      description: 'Vibrant, intuitive, and minimal digital expressions of your brand.',
      icon: Layout
    },
    {
      title: 'IT Support',
      description: 'Always-on experts ready to troubleshoot and optimize your systems.',
      icon: Headphones,
      tag: 'Free!'
    }
  ];

  const brandLogos = [
    { name: 'Netflix', logo: '/brands/netflix.svg' },
    { name: 'Apple Music', logo: '/brands/apple-music.svg' },
    { name: 'Amazon', logo: '/brands/amazon.svg' },
    { name: 'Spotify', logo: '/brands/spotify.svg' },
    { name: 'Google', logo: 'https://logo.clearbit.com/google.com' },
    { name: 'Microsoft', logo: 'https://logo.clearbit.com/microsoft.com' },
    { name: 'Adobe', logo: 'https://logo.clearbit.com/adobe.com' },
    { name: 'Salesforce', logo: 'https://logo.clearbit.com/salesforce.com' },
    { name: 'IBM', logo: 'https://logo.clearbit.com/ibm.com' },
    { name: 'Oracle', logo: 'https://logo.clearbit.com/oracle.com' }
  ];

  const consultingHighlights = [
    '24/7 Full Service Support',
    'Immediate Response',
    'Easy to approach us'
  ];

  const caseStudies = [
    {
      name: 'Development',
      badgePrimary: 'Web Development',
      badgeSecondary: 'Mobile Development',
      imagePrimary:
        'https://images.unsplash.com/photo-1517433456452-f9633a875f6f?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?auto=format&fit=crop&w=800&q=80',
      description:
        'Software development is a dynamic and rapidly evolving field that shapes the digital landscape. We design, create, deploy, and maintain modern applications and systems.'
    },
    {
      name: 'Woo Commerce',
      badgePrimary: 'Storefront Design',
      badgeSecondary: 'Conversion Optimization',
      imagePrimary:
        'https://images.unsplash.com/photo-1545239351-1141bd82e8a6?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1441986300917-64674bd600d8?auto=format&fit=crop&w=800&q=80',
      description:
        'We build immersive WooCommerce experiences that balance brand storytelling with intuitive purchasing journeys and robust integrations.'
    },
    {
      name: 'CRM Solutions',
      badgePrimary: 'Customer Insights',
      badgeSecondary: 'Automation Pipelines',
      imagePrimary:
        'https://images.unsplash.com/photo-1556740749-887f6717d7e4?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1587614382346-ac6c4593abfc?auto=format&fit=crop&w=800&q=80',
      description:
        'Our CRM programs unify sales, service, and marketing data to power personalization and operational efficiency across touchpoints.'
    },
    {
      name: 'Web Designing',
      badgePrimary: 'Brand Systems',
      badgeSecondary: 'Prototype Labs',
      imagePrimary:
        'https://images.unsplash.com/photo-1523475472560-d2df97ec485c?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1498050108023-c5249f4df085?auto=format&fit=crop&w=800&q=80',
      description:
        'We pair design thinking with motion, accessibility, and consistent UI systems to craft standout digital destinations.'
    },
    {
      name: 'IT Support',
      badgePrimary: 'Managed Services',
      badgeSecondary: 'Security Operations',
      imagePrimary:
        'https://images.unsplash.com/photo-1525182008055-f88b95ff7980?auto=format&fit=crop&w=800&q=80',
      imageSecondary:
        'https://images.unsplash.com/photo-1518770660439-4636190af475?auto=format&fit=crop&w=800&q=80',
      description:
        'Our 24/7 IT support covers proactive monitoring, rapid incident response, and lifecycle management for hybrid infrastructures.'
    }
  ];

  const [activeCase, setActiveCase] = useState(caseStudies[0]);
  const [isAnimating, setIsAnimating] = useState(false);

  const handleCaseChange = (nextCase) => {
    if (nextCase.name === activeCase.name || isAnimating) return;

    setIsAnimating(true);
    setTimeout(() => {
      setActiveCase(nextCase);
      setIsAnimating(false);
    }, 220);
  };

  const consultingSteps = [
    {
      title: 'Discovery and Analysis',
      description: "Perform an analysis of the client's existing IT systems."
    },
    {
      title: 'Tailored Solutions',
      description: 'Develop IT solutions based on the analysis phase.'
    },
    {
      title: 'Deployment and Support',
      description: 'Regularly communicate with our client to any concern.'
    }
  ];

  const processSteps = [
    {
      title: 'Brainstorming Ideas',
      description: 'Collaborative discovery sessions to surface high-impact opportunities.',
      icon: Brain
    },
    {
      title: 'Product Design',
      description: 'Design systems and prototypes that align brand, UX, and business goals.',
      icon: Palette
    },
    {
      title: 'Front-End Development',
      description: 'Interfaces engineered for performance, accessibility, and delight.',
      icon: Code2
    },
    {
      title: 'SEO Optimization',
      description: 'Data-driven improvements that help your product stay visible and competitive.',
      icon: Search
    },
    {
      title: 'Back-End Development',
      description: 'Robust, scalable services architected for security and reliability.',
      icon: Server
    },
    {
      title: 'Digital Marketing',
      description: 'Launch strategies that extend reach and drive measurable growth.',
      icon: Megaphone
    }
  ];

  const services = [
    {
      image: 'https://images.unsplash.com/photo-1521737604893-d14cc237f11d?auto=format&fit=crop&w=600&q=80',
      title: 'Software Development',
      description: 'End-to-end engineering for products, platforms, and modernization initiatives.'
    },
    {
      image: 'https://images.unsplash.com/photo-1525182008055-f88b95ff7980?auto=format&fit=crop&w=600&q=80',
      title: 'Web, Mobile & Desktop Apps',
      description: 'Responsive web, native mobile, and cross-platform desktop experiences your users love.'
    },
    {
      image: 'https://images.unsplash.com/photo-1518186285589-2f7649de83e0?auto=format&fit=crop&w=600&q=80',
      title: 'Custom ERP & CRM Systems',
      description: 'Purpose-built ERPs and CRMs that centralize data and automate everyday workflows.'
    },
    {
      image: 'https://images.unsplash.com/photo-1517430816045-df4b7de11d1d?auto=format&fit=crop&w=600&q=80',
      title: 'API & System Integration',
      description: 'Secure APIs and integrations that connect legacy, cloud, and third-party services.'
    },
    {
      image: 'https://images.pexels.com/photos/3183197/pexels-photo-3183197.jpeg?auto=compress&cs=tinysrgb&w=600&h=400&dpr=2',
      title: 'Website Design & Development',
      description: 'Brand-led websites crafted for performance, accessibility, and storytelling.'
    },
    {
      image: 'https://images.pexels.com/photos/1181406/pexels-photo-1181406.jpeg?auto=compress&cs=tinysrgb&w=600&h=400&dpr=2',
      title: 'Corporate, E-commerce & Portfolio Websites',
      description: 'High-converting sites for enterprises, retailers, and creative teams alike.'
    },
    {
      image: 'https://images.pexels.com/photos/6476589/pexels-photo-6476589.jpeg?auto=compress&cs=tinysrgb&w=600&h=400&dpr=2',
      title: 'SEO, Speed & Security Optimization',
      description: 'Technical optimizations to boost rankings, load times, and platform resilience.'
    },
    {
      image: 'https://images.pexels.com/photos/5473957/pexels-photo-5473957.jpeg?auto=compress&cs=tinysrgb&w=600&h=400&dpr=2',
      title: 'AI Automations',
      description: 'Design, build, and integrate AI copilots, automation workflows, and data insights.'
    }
  ];

  const teamFeatures = [
    {
      title: 'Diverse Expertise',
      description:
        'Seasoned engineers, designers, and strategists collaborate to tackle complex challenges across industries.'
    },
    {
      title: 'Collaborative Culture',
      description:
        'Transparent communication and cross-functional squads keep delivery fast, focused, and consistent.'
    },
    {
      title: 'Customer-Centric Approach',
      description:
        'Every engagement starts with discovery, aligning business goals with user needs for measurable outcomes.'
    },
    {
      title: 'Passion for Excellence',
      description:
        'Continuous improvement and rigorous QA ensure every release meets enterprise-grade expectations.'
    },
    {
      title: 'Empowered Learning',
      description:
        'Mentorship, guilds, and skill-building programs keep our teams sharp on emerging technologies.'
    },
    {
      title: 'Innovation and Creativity',
      description:
        'Ideation sprints and rapid prototyping unlock breakthrough solutions tailored to your roadmap.'
    }
  ];

  const showcaseHighlights = [
    'Managed services and products',
    'Flexibility and adaptability',
    'Competitive advantage'
  ];

  const portfolioShowcase = [
    {
      title: 'E-commerce platform development',
      caption: 'Global retail reimagined',
      background: 'https://images.unsplash.com/photo-1556740749-887f6717d7e4?auto=format&fit=crop&w=900&q=80',
      thumbnail: 'https://images.unsplash.com/photo-1529927066849-43bd0aae88b0?auto=format&fit=crop&w=400&q=80',
      theme: 'dark'
    },
    {
      title: 'SaaS integration suite',
      caption: 'Unified experience across cloud tools',
      background: 'https://images.unsplash.com/photo-1523475472560-d2df97ec485c?auto=format&fit=crop&w=900&q=80',
      thumbnail: 'https://images.unsplash.com/photo-1526662092594-e98c1e356d6a?auto=format&fit=crop&w=400&q=80',
      theme: 'light'
    },
    {
      title: 'Mobile platform development',
      caption: 'Intuitive journeys on every device',
      background: 'https://images.unsplash.com/photo-1512499617640-c2f999018b72?auto=format&fit=crop&w=900&q=80',
      thumbnail: 'https://images.unsplash.com/photo-1598327105666-5b89351aff97?auto=format&fit=crop&w=400&q=80',
      theme: 'light'
    }
  ];

  return (
    <div className="pt-20">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-primary-50 via-white to-primary-50 py-20 lg:py-32 overflow-hidden">
        <div className="absolute inset-0">
          <img
            src="https://images.unsplash.com/photo-1497366216548-37526070297c?auto=format&fit=crop&w=1920&q=80"
            alt="Hero background"
            className="w-full h-full object-cover opacity-5"
          />
        </div>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
          <div className="grid lg:grid-cols-2 gap-12 items-center">
            <div className="space-y-8 scroll-animate">
              <div className="inline-flex items-center bg-primary-100 text-primary-700 px-4 py-2 rounded-full text-sm font-medium">
                <span className="w-2 h-2 bg-primary-500 rounded-full mr-2 animate-pulse"></span>
                Welcome to Enginerds
              </div>
              <h1 className="text-5xl lg:text-7xl font-bold text-gray-900 leading-tight">
                Transform IT for your business, quality, reliability
              </h1>
              <p className="text-xl text-gray-600 leading-relaxed">
                We deliver innovative software solutions that drive business growth and digital transformation with cutting-edge technology.
              </p>
              <div className="flex flex-col sm:flex-row gap-4">
                <Link
                  to="/contact"
                  className="inline-flex items-center justify-center bg-primary-500 text-white px-8 py-4 rounded-lg font-semibold hover:bg-primary-600 transition-all duration-200 shadow-lg hover:shadow-xl"
                >
                  Get Started
                  <ArrowRight className="ml-2 w-5 h-5" />
                </Link>
                <button className="inline-flex items-center justify-center border-2 border-gray-300 text-gray-700 px-8 py-4 rounded-lg font-semibold hover:border-primary-500 hover:text-primary-500 transition-all duration-200">
                  <Play className="mr-2 w-5 h-5" />
                  Watch Demo
                </button>
              </div>
            </div>
            <div className="relative scroll-animate">
              <div className="relative rounded-2xl overflow-hidden shadow-2xl">
                <img
                  src="https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&w=800&q=80"
                  alt="Team collaboration"
                  className="w-full h-auto"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-charcoal-900/60 to-transparent"></div>
              </div>
              <div className="absolute -bottom-6 -left-6 bg-white rounded-xl shadow-xl p-6 max-w-xs">
                <div className="flex items-center space-x-4">
                  <div className="bg-green-100 rounded-full p-3">
                    <CheckCircle className="w-6 h-6 text-green-600" />
                  </div>
                  <div>
                    <p className="text-sm text-gray-600">Success Rate</p>
                    <p className="text-2xl font-bold text-gray-900">98%</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Logos */}
      <section className="py-16 bg-white overflow-hidden">
        <div className="relative scroll-animate">
          <div className="absolute inset-y-0 left-0 w-24 bg-gradient-to-r from-white via-white/80 to-transparent pointer-events-none"></div>
          <div className="absolute inset-y-0 right-0 w-24 bg-gradient-to-l from-white via-white/80 to-transparent pointer-events-none"></div>
          <div className="marquee-container py-2">
            <div className="marquee-content marquee-left space-x-6">
              {[...brandLogos, ...brandLogos].map((brand, index) => (
                <div
                  key={`${brand.name}-${index}`}
                  className="inline-flex h-24 w-40 items-center justify-center rounded-3xl bg-white/90 backdrop-blur border border-slate-100 shadow-[0_12px_30px_-18px_rgba(15,23,42,0.35)]"
                >
                  <img
                    src={brand.logo}
                    alt={`${brand.name} logo`}
                    className="max-h-12 w-auto object-contain"
                    loading="lazy"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Process Section */}
      <section className="relative py-20 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-dark via-charcoal-900 to-charcoal-800"></div>
        <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_top_left,_rgba(255,255,255,0.15),_transparent_45%),_radial-gradient(circle_at_bottom_right,_rgba(255,92,26,0.2),_transparent_55%)]"></div>
        <div className="relative max-w-[1200px] xl:max-w-[1320px] mx-auto px-4 sm:px-6 lg:px-10 xl:px-12 text-white">
          <div className="grid gap-12 lg:gap-16 xl:gap-20 lg:grid-cols-[minmax(0,1fr),minmax(0,1.7fr)] items-start">
            <div className="space-y-6 scroll-animate">
              <div className="uppercase tracking-[0.35em] text-primary-200 text-xs font-semibold">
                Our Model
              </div>
              <h2 className="text-4xl sm:text-5xl font-bold leading-tight">
                How we do
              </h2>
              <p className="text-lg text-primary-100/80 max-w-md">
                Save time and money with our powerful method, blending strategy, design, and engineering for measurable outcomes.
              </p>
              <Link
                to="/services"
                className="inline-flex items-center bg-primary-500 hover:bg-primary-400 text-dark font-semibold px-6 py-3 rounded-lg transition-all duration-200 shadow-lg shadow-primary-500/30"
              >
                Learn More
                <ArrowRight className="ml-2 h-5 w-5" />
              </Link>
            </div>

            <div className="relative lg:ml-auto w-full scroll-animate">
              <div className="pointer-events-none hidden lg:block absolute inset-6 xl:inset-8">
                <div className="absolute top-6 bottom-6 left-[33.333%] border-l border-dashed border-white/15"></div>
                <div className="absolute top-6 bottom-6 left-[66.666%] border-l border-dashed border-white/15"></div>
                <div className="absolute left-8 right-8 top-1/2 border-t border-dashed border-white/15"></div>
              </div>
              <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
                {processSteps.map((step, index) => {
                  const Icon = step.icon;
                  return (
                    <div
                      key={step.title}
                      className="relative group h-full rounded-2xl bg-charcoal-800/80 backdrop-blur border border-white/5 p-6 shadow-[0_20px_45px_-25px_rgba(15,23,42,0.8)]"
                    >
                      <div className="flex items-center justify-between mb-4">
                        <span className="inline-flex h-10 w-10 items-center justify-center rounded-full bg-primary-500/10 text-primary-200">
                          <Icon className="h-6 w-6" />
                        </span>
                        <span className="text-xs font-semibold uppercase tracking-wide text-white/50">
                          {index + 1 < 10 ? `0${index + 1}` : index + 1}
                        </span>
                      </div>
                      <h3 className="text-xl font-semibold text-white mb-2">
                        {step.title}
                      </h3>
                      <p className="text-sm text-white/70 leading-relaxed">
                        {step.description}
                      </p>
                      <div className="absolute -bottom-2 left-6 right-6 h-px bg-gradient-to-r from-transparent via-white/20 to-transparent"></div>
                    </div>
                  );
                })}
              </div>
              <div className="hidden sm:block pointer-events-none absolute inset-10 border border-dashed border-white/10 rounded-3xl"></div>
            </div>
          </div>
        </div>
      </section>

      {/* Offerings Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid gap-10 lg:grid-cols-[1.1fr,1fr] items-start">
            <div className="space-y-4 scroll-animate">
              <p className="text-xs font-semibold tracking-[0.35em] uppercase text-primary-500">
                What we're offering
              </p>
              <h2 className="text-4xl sm:text-5xl font-bold text-gray-900 leading-snug">
                Dealing in all professional IT services.
              </h2>
            </div>
            <p className="text-base sm:text-lg text-gray-600 leading-relaxed scroll-animate">
              One fundamental aspect of IT services is infrastructure management. This involves the design,
              implementation, and maintenance of the hardware, software, networks, and servers.
            </p>
          </div>

          <div className="mt-12 grid gap-6 md:grid-cols-2 xl:grid-cols-5 scroll-animate">
            {offerings.map((item) => {
              const Icon = item.icon;
              return (
                <div
                  key={item.title}
                  className="relative h-full rounded-3xl border border-slate-100 bg-white p-8 shadow-[0_24px_45px_-35px_rgba(15,23,42,0.55)] transition-all duration-200 hover:-translate-y-1 hover:shadow-[0_30px_60px_-35px_rgba(15,23,42,0.65)]"
                >
                  {item.tag && (
                    <span className="absolute -top-3 right-6 inline-flex items-center rounded-full bg-primary-500 px-3 py-1 text-xs font-semibold uppercase tracking-wide text-white shadow-lg">
                      {item.tag}
                    </span>
                  )}
                  <span className="inline-flex h-12 w-12 items-center justify-center rounded-2xl bg-primary-50 text-primary-500 mb-6">
                    <Icon className="h-6 w-6" />
                  </span>
                  <h3 className="text-xl font-semibold text-gray-900 mb-3">{item.title}</h3>
                  <p className="text-sm text-gray-600 leading-relaxed">{item.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Case Studies Section */}
      <section className="py-20 bg-slate-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center space-y-4 scroll-animate">
            <p className="text-xs font-semibold tracking-[0.35em] uppercase text-primary-500">
              Case Studies
            </p>
            <h2 className="text-4xl sm:text-5xl font-bold text-gray-900">
              Detailing of our products
            </h2>
          </div>

          <div className="mt-10 flex flex-wrap justify-center gap-3 scroll-animate">
            {caseStudies.map((study) => (
              <button
                key={study.name}
                type="button"
                onClick={() => handleCaseChange(study)}
                aria-pressed={activeCase.name === study.name}
                className={`px-6 py-3 rounded-full text-sm font-semibold transition-all duration-200 shadow-sm focus:outline-none focus-visible:ring-2 focus-visible:ring-primary-500/70 focus-visible:ring-offset-2 focus-visible:ring-offset-slate-50 ${
                  activeCase.name === study.name
                    ? 'bg-gray-900 text-white shadow-lg'
                    : 'bg-white text-gray-700 hover:bg-gray-100'
                }`}
              >
                {study.name}
              </button>
            ))}
          </div>

          <div
            className={`mt-12 grid gap-6 lg:grid-cols-[1.05fr,1.05fr,0.9fr] items-stretch transition-all duration-300 ${
              isAnimating ? 'opacity-0 translate-y-3' : 'opacity-100 translate-y-0'
            } scroll-animate`}
          >
            <div className="relative overflow-hidden rounded-3xl bg-white shadow-[0_24px_55px_-35px_rgba(15,23,42,0.55)] transition-transform duration-300">
              <div className="flex items-center justify-between px-6 pt-6">
                <span className="inline-flex items-center rounded-full bg-primary-100 text-primary-600 font-medium text-xs px-3 py-1">
                  {activeCase.badgePrimary}
                </span>
              </div>
              <img
                src={activeCase.imagePrimary}
                alt={`${activeCase.name} primary visual`}
                className="mt-4 h-80 w-full object-cover"/>
            </div>

            <div className="relative overflow-hidden rounded-3xl bg-white shadow-[0_24px_55px_-35px_rgba(15,23,42,0.55)] transition-transform duration-300">
              <div className="flex items-center justify-between px-6 pt-6">
                <span className="inline-flex items-center rounded-full bg-primary-100 text-primary-600 font-medium text-xs px-3 py-1">
                  {activeCase.badgeSecondary}
                </span>
              </div>
              <img
                src={activeCase.imageSecondary}
                alt={`${activeCase.name} secondary visual`}
                className="mt-4 h-80 w-full object-cover"/>
            </div>

            <div className="rounded-3xl bg-white p-10 shadow-[0_24px_55px_-35px_rgba(15,23,42,0.55)] flex flex-col transition-transform duration-300">
              <span className="inline-flex h-12 w-12 items-center justify-center rounded-2xl bg-primary-50 text-primary-500 mb-6">
                <ArrowRight className="h-6 w-6" />
              </span>
              <h3 className="text-2xl font-semibold text-gray-900 mb-4">{activeCase.name}</h3>
              <p className="text-sm text-gray-600 leading-relaxed flex-1">{activeCase.description}</p>
              <Link
                to="/portfolio"
                className="mt-6 inline-flex items-center justify-center self-start rounded-full bg-primary-500 px-4 py-2 text-sm font-semibold text-white transition-colors duration-200 hover:bg-primary-400"
              >
                Learn more
                <ArrowUpRight className="ml-2 h-4 w-4" />
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Consulting Excellence Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid gap-12 lg:grid-cols-[1.1fr,1.1fr] items-start">
            <div className="space-y-6 scroll-animate">
